import { Component } from '@angular/core';

@Component({
  selector: 'app-tab3',
  templateUrl: 'tab3.page.html',
  styleUrls: ['tab3.page.scss']
})
export class Tab3Page {

  constructor() {}
  a: number[][] = [];
  n: number = 0;

  arrayras(n: any) {

    this.a = [];

    try {

      // Перетворюємо розмірність у ціле число

      this.n = parseInt(n);

      // Валідування введення

      if (isNaN(this.n) === true) {

        // Якщо є помилка в введені викидуємо помилку

        throw new Error('Parameter is not a number!');

      }

      // Якщо псе

      if (n <= 0) {

        // Якщо є помилка в введені викидуємо помилку

        throw new Error('Parameter less than zero');

      }

      //оголошуємо змінні для циклу

      let i: number = 0, j: number = 0;

      //Створюємо об'єкт масив

      this.a = Array.from(Array(this.n), () => Array(this.n));

      console.log('Array created');

      for (i = 0; i < this.n; i++) {

        //Створюємо рядок матриці

        this.a[i] = Array(this.n);

        //У циклі заповнюємо матрицю

        for (j = 0; j < this.n; j++) {

          //обраховуємо значення

          let aa: number = Math.pow(-1, i) * (i + 2 * j) / 3;

          //округлюємо значення та записуємо його до масиву

          this.a[i][j] = parseFloat(aa.toFixed(2));

        }

      }

    } catch (error) {

      console.log(error);

    }

  }

  getColor(b: number) {

    //якщо число додатне повертаємо зелений інакше червоний

    return b % 2 === 0 ? '--ion-color-primary' : (b > 0 ? 'green' : 'red');

  }
}
