import {Component} from '@angular/core';

@Component({
  selector: 'app-tab1', templateUrl: 'tab1.page.html', styleUrls: ['tab1.page.scss']
})
export class Tab1Page {

  constructor() {
  }

  result = 0;

  calc(num1: any, num2: any, num3: any) {
    let result: number = 0;
    try {
      let n1 = parseFloat(num1);
      let n2 = parseFloat(num2);
      let n3 = parseFloat(num3);
      if (isNaN(n1) || isNaN(n2) || isNaN(n3)) {
        throw new Error('Not a number in input');
      }
      if (n1 > 0 || n2 > 0 || n3 > 0) {
        result = (n1 + n2 + n3) ** 2;
      } else {
        result = n1 ** 2 + n2 ** 2 + n3 ** 2;
      }
    } catch (error) {
      console.error(error);
      // @ts-ignore
      document.getElementById("output").innerText = "Ви ввели не число."
    } finally {
      this.result = result;
    }

  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Lab 1, Іванченко Володимир
    </ion-title>
  </ion-toolbar>
</ion-header>
<ion-content [fullscreen]="true">
  <ion-card>
    <ion-card-header>
      <ion-card-title>
        Завдання №1
      </ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <p>Задано три числа. Якщо хоч одне з них додатне, то знайти квадрат суми. В іншому випадку – суму квадратів.</p>
      <ion-item>
        <ion-label position="floating">a:</ion-label>
        <ion-input type="number" id="a" value="" #in_a></ion-input>
      </ion-item>
      <ion-item>
        <ion-label position="floating">b:</ion-label>
        <ion-input type="number" id="b" value="" #in_b></ion-input>
      </ion-item>
      <ion-item>
        <ion-label position="floating">c:</ion-label>
        <ion-input type="number" id="c" value="" #in_c></ion-input>
      </ion-item>
        <ion-button expand="block" (click)="calc(in_a.value, in_b.value, in_c.value)">Обчислити</ion-button>
    </ion-card-content>
  </ion-card>
  <ion-card>
    <ion-card-header>
      <ion-card-title>
        Результат
      </ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <ion-label id="output">{{result}}</ion-label>
    </ion-card-content>
  </ion-card>
</ion-content>
