import { Component } from '@angular/core';

@Component({
  selector: 'app-tab2',
  templateUrl: 'tab2.page.html',
  styleUrls: ['tab2.page.scss']
})
export class Tab2Page {

  constructor() {}

  result :number = 0;
  finder(num1:any, num2:any){
    try{
      let n1 = parseInt(num1);
      let n2 = parseInt(num2);
      if (isNaN(n1) || isNaN(n2)) {
        throw new Error('Введено не число');
      }
      if (n2 < n1) {
        throw new Error('b повинне бути більше a.');
      }
      let firstDivisible = 0;

    }catch (error){
      console.error(error);
      // @ts-ignore
      document.getElementById("output").innerText = error.toString();
    }
    this.result = 0;
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Lab 1, Іванченко Володимир
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Tab 2</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-card>
    <ion-card-header>
      <ion-card-title>
        Завдання №2
      </ion-card-title>
      На заданому проміжку чисел [a,b] знайдіть кількість чисел, які кратні 29, парні і при діленні на 5 дають в остачі
      2.
    </ion-card-header>
    <ion-card-content>
      <ion-item>
        <ion-label position="floating">a:</ion-label>
        <ion-input type="number" id="a" value="" #in_a></ion-input>
      </ion-item>
      <ion-item>
        <ion-label position="floating">b:</ion-label>
        <ion-input type="number" id="b" value="" #in_b></ion-input>
      </ion-item>
      <ion-button expand="block">Обчислити</ion-button>
    </ion-card-content>
  </ion-card>
  <ion-card>
    <ion-card-header>
      <ion-card-title>
        Результат
      </ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <ion-label id="output" (click)="finder(in_a.value, in_b.value)">{{result}}</ion-label>
    </ion-card-content>
  </ion-card>
</ion-content>
